import React from 'react';
import { ImageBackground, StyleSheet, View, Text, Button, TouchableOpacity, Image, TouchableWithoutFeedback, ScrollView ,KeyboardAvoidingView} from 'react-native';
import { Feather, Entypo } from "@expo/vector-icons";
import SearchBar from "react-native-dynamic-search-bar";

function HomeScreen(props) {
    return (
        <View style={styles.container}>
            <View style = {styles.topContainerView}>
                <Image style={styles.greetingImage} source={require('../assets/Home/greeting.png')}/>
                <Text style={styles.welcomeText}>Welcome Back,</Text>
            </View>
            <Text style={styles.nameText}>Nestor Jerez</Text>
            <Image style={styles.userImage} source={require('../assets/Home/userImage.png')}/>
            <Image style={styles.statusImage} source={require('../assets/Home/onlineIcon.png')}/>
        <View style={styles.rect}> 
         </View>
            <SearchBar
                style = {styles.searchBarStyle}
                fontColor="#c6c6c6"
                iconColor="#c6c6c6"
                shadowColor="#282828"
                cancelIconColor="#c6c6c6"
                backgroundColor="#353d5e"
                placeholder="Search here"
                onChangeText={(text) => this.filterList(text)}
                onSearchPress={() => console.log("Search Icon is pressed")}
                onClearPress={() => this.filterList("")}
                onPress={() => alert("onPress")}
                />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "rgba(44,39,29,1)"
    },
    rect: {
      width: '100%',
      height: '100%',
      backgroundColor: "#E6E6E6",
      marginTop: 120,

    },
    topContainerView :{
        top :70,
        flexDirection : 'row',
        height : 30,
        left : 30,
        width : '60%',
        position : 'absolute'
    },
    greetingImage :{
        width : 14.9,
        height : 14.9
    },
    welcomeText : {
        fontWeight : 'bold',
        fontSize : 14,
        color : 'white',
        marginLeft : 20,
        position : 'absolute'

    },
    nameText : {
        top : '10%',
        fontWeight : 'bold',
        fontSize : 28,
        color : 'white',
        marginLeft : 30,
        marginVertical : 10
    },
    userImage : {
        top : '8%',
        width : 55,
        height : 55,
        left : '80%',
        position : 'absolute'
    },
    statusImage: {
        width : 12,
        height : 12,
        left : '79%',
        top : '7.5%',
        position : 'absolute'
    },
    searchBarStyle :{
        top : '40%',
        width : '100%',
        height : 60,
        backgroundColor : 'red'
    }

  });
export default HomeScreen;
